screen.userinput:
for package in $(grep -v '^#' ../pythonhosted-files.md5 | awk '{print $2}') do name=$(echo $package|sed 's/-[[:digit:]].*$//')
# Don't try to install the package if it already installed installed_version=$(pip3 show $name 2> /dev/null | grep Version: | awk '{print $2}') if [ -n $installed_version ]; then [ $(echo $package | grep -q $installed_version) ] && continue fi
packagedir=${package%.tar.?z*} tar -xf $package pushd $packagedir pip3 wheel -w dist --no-build-isolation --no-deps $PWD sudo pip3 install --no-index --find-links dist --no-cache-dir \ --no-user --upgrade $name popd rm -rf $packagedir done
